import requests
import json

caido_url = "http://127.0.0.1:8080"

print("1. Checking health endpoint in detail:")
health_url = f"{caido_url}/health"
try:
    response = requests.get(health_url, timeout=10)
    if response.status_code == 200:
        data = response.json()
        print(f"Health endpoint data: {json.dumps(data, indent=2)}")
except Exception as e:
    print(f"Error: {e}")

print("\n" + "="*50 + "\n")
print("2. Trying GraphQL introspection:")

introspection_query = {
    "query": """
    query IntrospectionQuery {
      __schema {
        queryType { name }
        mutationType { name }
        subscriptionType { name }
        types {
          ...FullType
        }
        directives {
          name
          description
          locations
          args {
            ...InputValue
          }
        }
      }
    }
    fragment FullType on __Type {
      kind
      name
      description
      fields(includeDeprecated: true) {
        name
        description
        args {
          ...InputValue
        }
        type {
          ...TypeRef
        }
        isDeprecated
        deprecationReason
      }
      inputFields {
        ...InputValue
      }
      interfaces {
        ...TypeRef
      }
      enumValues(includeDeprecated: true) {
        name
        description
        isDeprecated
        deprecationReason
      }
      possibleTypes {
        ...TypeRef
      }
    }
    fragment InputValue on __InputValue {
      name
      description
      type { ...TypeRef }
      defaultValue
    }
    fragment TypeRef on __Type {
      kind
      name
      ofType {
        kind
        name
        ofType {
          kind
          name
          ofType {
            kind
            name
            ofType {
              kind
              name
              ofType {
                kind
                name
                ofType {
                  kind
                  name
                  ofType {
                    kind
                    name
                  }
                }
              }
            }
          }
        }
      }
    }
    """
}

graphql_url = f"{caido_url}/graphql"
try:
    response = requests.post(graphql_url, json=introspection_query, timeout=10)
    print(f"GraphQL introspection status: {response.status_code}")
    if response.status_code == 200:
        try:
            data = response.json()
            if "data" in data:
                print("GraphQL introspection successful!")
                types = data["data"]["__schema"]["types"]
                print(f"Found {len(types)} GraphQL types")

                flag_types = [t for t in types if "flag" in t.get("name", "").lower() or "ctf" in t.get("name", "").lower()]
                if flag_types:
                    print(f"\nFound flag-related types:")
                    for t in flag_types:
                        print(f"  - {t['name']}: {t.get('description', 'No description')}")
            else:
                print(f"GraphQL response: {json.dumps(data, indent=2)[:500]}")
        except json.JSONDecodeError:
            print(f"Response: {response.text[:500]}")
    else:
        print(f"Response: {response.text[:500]}")
except Exception as e:
    print(f"Error: {e}")

print("\n" + "="*50 + "\n")
print("3. Trying simple GraphQL queries:")

simple_queries = [
    {"query": "query { __typename }"},
    {"query": "query { flag }"},
    {"query": "query { ctf }"},
    {"query": "query { challenge }"},
    {"query": "query { flags { id name } }"},
    {"query": "query { challenges { id name } }"}
]

for i, query in enumerate(simple_queries):
    try:
        response = requests.post(graphql_url, json=query, timeout=5)
        if response.status_code == 200:
            data = response.json()
            if "errors" not in data:
                print(f"Query {i+1} succeeded: {json.dumps(data, indent=2)}")
            elif "flag" in str(data).lower() or "ctf" in str(data).lower():
                print(f"Query {i+1} returned: {json.dumps(data, indent=2)}")
    except Exception as e:
        print(f"Query {i+1} error: {type(e).__name__}")

