import requests

print("Testing local services:")
services = [
    ("http://127.0.0.1:8000", "Python service port 8000"),
    ("http://127.0.0.1:8080", "Caido-cli service port 8080"),
    ("http://127.0.0.1:9222", "Chrome debugger port 9222"),
    ("http://127.0.0.1:5901", "VNC server port 5901"),
]

for url, description in services:
    print(f"\nTesting {description} ({url}):")
    try:
        response = requests.get(url, timeout=5)
        print(f"  Status: {response.status_code}")
        if response.status_code < 400:
            print(f"  Content type: {response.headers.get('content-type', 'Unknown')}")
            print(f"  Content preview: {response.text[:200]}")
    except requests.exceptions.RequestException as e:
        print(f"  Error: {type(e).__name__}")
    except Exception as e:
        print(f"  Unexpected error: {e}")

